import React, { useState } from 'react';
import './GamePage.css';

const BINGO_LETTERS = ['B', 'I', 'N', 'G', 'O'];

const GamePage = () => {
    const [selectedNumber, setSelectedNumber] = useState(1);
    const [bingoCard, setBingoCard] = useState(generateBingoCard(1));
    const [isCardSelected, setIsCardSelected] = useState(false);
    const [calledNumbers, setCalledNumbers] = useState([]);
    const [timer, setTimer] = useState(null);
    const [currentCall, setCurrentCall] = useState('');

    const handleNumberChange = (e) => {
        setSelectedNumber(e.target.value);
        setBingoCard(generateBingoCard(e.target.value));
    };

    const handleCardSelect = () => {
        setIsCardSelected(true);
        startTimer();
    };

    const startTimer = () => {
        const interval = setInterval(() => {
            const letter = BINGO_LETTERS[Math.floor(Math.random() * BINGO_LETTERS.length)];
            const number = Math.floor(Math.random() * 75) + 1;
            const call = `${letter}${number}`;
            setCurrentCall(call);
            setCalledNumbers(prev => [...prev, call]);
        }, 3000);
        setTimer(interval);
    };

    useEffect(() => {
        return () => {
            clearInterval(timer);
        };
    }, [timer]);

    return (
        <div>
            {!isCardSelected ? (
                <div>
                    <label>Select a number between 1-200:</label>
                    <input 
                        type="number" 
                        value={selectedNumber} 
                        onChange={handleNumberChange} 
                        min="1" 
                        max="200"
                    />
                    <button onClick={handleCardSelect}>Select Card</button>
                    <div className="bingo-card">
                        <div className="bingo-header">
                            {BINGO_LETTERS.map((letter, index) => (
                                <div key={index} className="bingo-header-cell">{letter}</div>
                            ))}
                        </div>
                        <div className="bingo-body">
                            {bingoCard.map((row, rowIndex) => (
                                <div key={rowIndex} className="bingo-row">
                                    {row.map((cell, cellIndex) => (
                                        <div key={cellIndex} className="bingo-cell">
                                            {cell === '★' ? '★' : cell}
                                        </div>
                                    ))}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            ) : (
                <div>
                    <h3>Game in progress</h3>
                    <p>Current Call: {currentCall}</p>
                    <div className="bingo-card">
                        {/* Bingo Card Rendering as Before */}
                    </div>
                </div>
            )}
        </div>
    );


  const generateBingoCard = (num) => {
    const card = Array.from({ length: 5 }, () =>
      Array.from({ length: 5 }, () => Math.floor(Math.random() * 100) + 1)
    );
    card[2][2] = '★'; // Middle cell as a star
    setBingoCard(card);
  };

  const handleSelect = (e) => {
    const num = parseInt(e.target.value, 10);
    setSelectedNumber(num);
    generateBingoCard(num);
  };

  return (
    <div className="game-page">
      <h2>Select a Bingo Card</h2>
      <input
        type="number"
        placeholder="Enter a number between 1-200"
        min="1"
        max="200"
        value={selectedNumber || ''}
        onChange={handleSelect}
      />
      {bingoCard && (
        <div className="bingo-card">
          <div className="bingo-header">
            <span>B</span>
            <span>I</span>
            <span>N</span>
            <span>G</span>
            <span>O</span>
          </div>
          {bingoCard.map((row, rowIndex) => (
            <div key={rowIndex} className="bingo-row">
              {row.map((number, colIndex) => (
                <div key={colIndex} className="bingo-cell">
                  {number}
                </div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default GamePage;
